    private void btnSelectYearActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSelectYearActionPerformed
        //Εμφάνισε στοιχείο επιλογής μήνα
        jMonthChooser1.setVisible(true);
        //Ορισμός έτους
        jTextField1.setText (String.valueOf(jYearChooser1.getYear()));
    }//GEN-LAST:event_btnSelectYearActionPerformed

