    private void btnShowDataActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnShowDataActionPerformed
        // Εμφάνιση οθόνης με τα δεδομένα που έχει επιλέξει ο χρήστης
        JokerViewMonthlyFrame jf21;
        jf21 = new JokerViewMonthlyFrame(jTextField1.getText());
        jf21.setVisible(true);
        dispose();     
    }//GEN-LAST:event_btnShowDataActionPerformed

    private void btnSelectMonthActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSelectMonthActionPerformed
        // Επιλογή μήνα και μορφοποίηση
        DecimalFormat formatter = new DecimalFormat("00");
        String month = formatter.format(jMonthChooser1.getMonth()+1);
        jTextField1.setText(String.valueOf(jYearChooser1.getYear())+ "-" + month);
    }//GEN-LAST:event_btnSelectMonthActionPerformed

